import React from 'react';
import './Messages.css';

import ScrollTomBottom from 'react-scroll-to-bottom';

const Messages = (props) => {
  const { messages, name } = props;
  return (
    <ScrollTomBottom>
      {messages.map((msg, idx) => (
        <div key={idx}>
          <Message message={msg} name={name} />
        </div>
      ))}
    </ScrollTomBottom>
  );
};

export default Messages;
